package Chap4;

public class ExEnhacedArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] str={"Arun","Raju","Ramu","Arvind","Ashok"};
		int i;
	for(i=0;i<5;i++)
		{
			System.out.println(str[i]);
		}
		int[] x={10,20,30,40,50};
		
		for(i=0;i<5;i++)
		{
			System.out.println(+x[i]);
		}
	}

}
